exports.map = function(app, map, dir) {
	Object.keys(map).forEach(function(key) {
		var _key = key.split(/\s+/);
		var _value = map[key].split('.');
		var method = _key[0].toLowerCase();
		var path = _key[1];
		var func = require(dir + '/' + _value[0]);
		app[method](path, func[_value[1]]);
	});
};